//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import MovieList from "./MovieList";
import Paper from "@mui/material/Paper";
import {itemData} from "./itemData"
import EditIcon from '@mui/icons-material/Edit';
import IconButton from '@mui/material/IconButton';
import ImageListItemBar from '@mui/material/ImageListItemBar';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Checkbox from '@mui/material/Checkbox';
import Avatar from '@mui/material/Avatar';

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default function AccountPage(props) {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [checked, setChecked] = React.useState([1]);

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <div>
      <Box sx={{ width: 1 }}>
      <div>
      <Dialog
        open={open}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle>{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-slide-description">
            Let Google help apps determine location. This means sending anonymous
            location data to Google, even when no apps are running.
          </DialogContentText><List dense sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
      {[0, 1, 2, 3].map((value) => {
        const labelId = `checkbox-list-secondary-label-${value}`;
        return (
          <ListItem
            key={value}
            secondaryAction={
              <Checkbox
                edge="end"
                onChange={handleToggle(value)}
                checked={checked.indexOf(value) !== -1}
                inputProps={{ 'aria-labelledby': labelId }}
              />
            }
            disablePadding
          >
            <ListItemButton>
              <ListItemAvatar>
                <Avatar
                  alt={`Avatar n°${value + 1}`}
                  src={`/static/images/avatar/${value + 1}.jpg`}
                />
              </ListItemAvatar>
              <ListItemText id={labelId} primary={`Line item ${value + 1}`} />
            </ListItemButton>
          </ListItem>
        );
      })}
    </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Disagree</Button>
          <Button onClick={handleClose}>Agree</Button>
        </DialogActions>
      </Dialog>
    </div>
        <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={2}>
          <Box gridColumn="span 3">
            <Item>
              <Typography variant="h5" component="h5">
                {props.accountName}'s Favorite Movie
              </Typography>
              <ImageListItem key="favorite">
                <img
                  src={`https://m.media-amazon.com/images/M/MV5BZjZjMzQ2ZmUtZWEyZC00NWJiLWFjM2UtMzhmYzZmZDcxMzllXkEyXkFqcGdeQXVyNTA4NzY1MzY@._V1_FMjpg_UX1000_.jpg`}
                  srcSet={`https://m.media-amazon.com/images/M/MV5BZjZjMzQ2ZmUtZWEyZC00NWJiLWFjM2UtMzhmYzZmZDcxMzllXkEyXkFqcGdeQXVyNTA4NzY1MzY@._V1_FMjpg_UX1000_.jpg`}
                  alt={"Image"}
                  loading="lazy"


                />
                <ImageListItemBar
                sx={{background:'rgba(0,0,0,0)',}}
            actionIcon={
              <IconButton
              sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                aria-label={`Edit Movie`}
                onClick={handleClickOpen}
              >
                <EditIcon />
              </IconButton>
            }
          />
              </ImageListItem>

              Seinfeld
            </Item>
          </Box>
          <Box gridColumn="span 9">
            <Item>
              <Typography variant="h5" align='left' component="h5">
                {props.accountName}'s Favorite Movies
              </Typography>
              <MovieList itemData={itemData.slice(0, 8)}/>
              <Typography variant="h5" align='left' component="h5">
                {props.accountName}'s Want to Watch
              </Typography>
              <MovieList itemData={itemData.slice(9, 17)}/>
              <Typography variant="h5" align='left' component="h5">
                {props.accountName} has Watched
              </Typography>
              <MovieList itemData={itemData.slice(18, 26)}/>
              </Item>
          <br></br>
            <Item>
              <Typography variant="h5" align='left' component="h5">
                {props.accountName}'s Movie Stats
              </Typography>
              Seinfeld
            </Item>
        </Box>
          </Box>

      </Box>
    </div>
  );
}
