//import logo from "./logo.svg";
import "./App.css";
import * as React from "react";
import CssBaseline from "@mui/material/CssBaseline";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import GridPage from "./components/GridPageContents/GridPage";
import TwentyQ from "./components/TwentyQ/TwentyQ";
import Account from "./components/Account/Account";
import Register from "./components/Account/Register";
import Movie from "./components/MovieDetail/Movie";
import axios from "axios";

export default class App extends React.Component {

  state = {
    user:""
  }
  componentDidMount() {
    console.log(this.props);
    axios.get("/currentUser").then((res) => {
      console.log(res["data"]["username"]);
      const user = res["data"]["username"];
      this.setState({ user: user});
    });
    console.log(this.state);
  }
  componentDidUpdate() {
    axios.get("/currentUser").then((res) => {
      const user = res["data"]["username"];
      this.setState({ user: user});
    });
  }

  render() {

  return (
    <div className="App">
      <link
        rel="stylesheet"
        href="https://fonts.googleapis.com/css?family=Roboto:300,400,500,700&display=swap"
      />
      <link
        rel="stylesheet"
        href="https://fonts.googleapis.com/icon?family=Material+Icons"
      />
      <header className="App-header">
        <CssBaseline enableColorScheme />

        <BrowserRouter>
          <Routes>
            <Route path="/" element={<GridPage user={this.state.user}/>} />
            <Route path="movieList" element={<GridPage user={this.state.user}/>} />
            <Route path="twentyQ" element={<TwentyQ />} />
            <Route path="account" element={<Account user={this.state.user}/>} />
            <Route path="register" element={<Register user={this.state.user}/>} />
            <Route path="movieDetails/:id" element={<Movie />} />
          </Routes>
        </BrowserRouter>
      </header>
    </div>
  );
}
}
