//import logo from "./logo.svg";
import * as React from "react";
import SideMenu from "../SideMenu/SideMenu";
import PageContents from "./PageContents";
import Container from "@mui/material/Container";
import { Box } from "@mui/system";
import { styled } from "@mui/material/styles";
import TopBar from "./TopBar";
const DrawerHeader = styled("div")(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  justifyContent: "flex-end",
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
}));

export default function GridPage(props) {
  const [open, setOpen] = React.useState(false);
  const [genreName, setGenreName] = React.useState([]);
  const [languageName, setLanguageName] = React.useState(["English"]);
  const [rating, setRating] = React.useState(() => [
    "G",
    "PG",
    "PG-13",
    "R",
    "NC-17",
    "NR",
  ]);
  const [year, setYear] = React.useState([1888, new Date().getUTCFullYear()]);
  const [runTime, setRunTime] = React.useState([75, 180]);
  const [streamingService, setStreamingService] = React.useState(() => [
    "Netflix",
  ]);
  const [streamIt, setStreamIt] = React.useState(false);
  const [seenIt, setSeenIt] = React.useState(false);
  const [sortingMethod, setSortingMethod] = React.useState(1);
  const [sortingDirection, setSortingDirection] = React.useState(false);
  return (
    <div>
      <TopBar setOpen={setOpen} open={open} />
      <SideMenu
        setOpen={setOpen}
        open={open}
        genreName={genreName}
        setGenreName={setGenreName}
        languageName={languageName}
        setLanguageName={setLanguageName}
        rating={rating}
        setRating={setRating}
        year={year}
        setYear={setYear}
        runTime={runTime}
        setRunTime={setRunTime}
        streamingService={streamingService}
        setStreamingService={setStreamingService}
        streamIt={streamIt}
        setStreamIt={setStreamIt}
        seenIt={seenIt}
        setSeenIt={setSeenIt}
        sortingMethod={sortingMethod}
        setSortingMethod={setSortingMethod}
        sortingDirection={sortingDirection}
        setSortingDirection={setSortingDirection}
      />
      <Container maxWidth="xl">
        <Box component="main" sx={{ flexGrow: 1, paddingLeft: 8 }}>
          <DrawerHeader />
          <PageContents
            genreName={genreName}
            languageName={languageName}
            rating={rating}
            year={year}
            runTime={runTime}
            streamingService={streamingService}
            streamIt={streamIt}
            seenIt={seenIt}
            sortingMethod={sortingMethod}
            sortingDirection={sortingDirection}
          />
        </Box>
      </Container>
    </div>
  );
}
