//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import { styled } from "@mui/material/styles";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';

function createData(name, value) {
  return { name, value};
}



const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

function getLanguages(languages) {
    let languagesText = ""
    for (var i = 0; i < languages.length-1; i++) {
        languagesText+=languages[i].english_name+', '
    } 
    if(languages.length != 0) {
    languagesText+=languages[languages.length].english_name
    }
    return languagesText;
  }


export default function MovieDetail(props) {
  const handleClickOpen = () => {};
  const rows = [
  createData('Budget', props.movie.budget),
  createData('Popularity', props.movie.popularity),
  createData('Revenue', props.movie.revenue),
  createData('Release Date', props.movie.release_date),
  createData('Language', getLanguages(props.movie.spoken_languages)),
  createData('Status', props.movie.status),
];
  return (
    <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={2}>
      <Box gridColumn="span 3">
        <Item>
          <ImageListItem key="favorite">
            <img
              src={`https://image.tmdb.org/t/p/w500${props.movie.poster_path}`}
              srcSet={`https://image.tmdb.org/t/p/w500${props.movie.poster_path}`}
              alt={"Image"}
              loading="lazy"
            />
            <ImageListItemBar
              sx={{ background: "rgba(0,0,0,0)" }}
              actionIcon={
                <div>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                    onClick={handleClickOpen}
                  >
                    <VisibilityIcon />
                  </IconButton>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                    onClick={handleClickOpen}
                  >
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                    onClick={handleClickOpen}
                  >
                    <ThumbDownAltIcon />
                  </IconButton>
                </div>
              }
            />
          </ImageListItem>
          {props.movie.title}
        </Item>
      </Box>
      <Box gridColumn="span 9">
        <Item>
          <Typography variant="h5" align="left" component="h5">
            Movie Stats
          </Typography>
          <Typography variant="h5" align="left" component="h5">
            Budget: {props.movie.budget}
          </Typography>
          <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.value}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
        </Item>
      </Box>
    </Box>
  );
}
