import * as React from "react";
import ImageList from "@mui/material/ImageList";
import ImageListItem from "@mui/material/ImageListItem";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import IconButton from "@mui/material/IconButton";
import LocalMoviesIcon from "@mui/icons-material/LocalMovies";
import { movieList } from "./MovieList";
import axios from "axios";

function getNumberCols() {
  const { innerWidth: width } = window;
  return Math.floor(width / 200);
}

function makeSearch(props) {
  let cert=""
  console.log(props)
  for (let i = 0; i < props.rating.length-1; i++) {
    cert+=props.rating[i]+'|'
  }
  cert+=props.rating[props.rating.length-1]
  console.log(cert)
  return "/filters/"+cert+'/'+props.year[0]+'/'+props.year[1]+'/'+props.runtime[0]+'/'+props.runtime[1];
}

export default class PageContents extends React.Component {
  state = {
    movies: [],
    oldprops: {}
  };

  componentDidMount() {
    console.log(this.props)
    axios.get(makeSearch(this.props)).then((res) => {
      console.log(res["data"]["results"]);
      const movies = res["data"]["results"];
      const oldprops = this.props
      this.setState({ movies, oldprops });
    });
    console.log(this.state)
  }
  componentDidUpdate() {
    if(this.props != this.state.oldprops)
    {
    console.log(this.props)
    axios.get(makeSearch(this.props)).then((res) => {
      console.log(res["data"]["results"]);
      const movies = res["data"]["results"];
      const oldprops = this.props
      this.setState({ movies, oldprops });
    });
    console.log(this.state)
  }
  }

  render() {
    return (
      <ImageList cols={getNumberCols()}>
        {this.state.movies.map((item) => (
          <ImageListItem key={item.poster_path}>
            <img
              src={`https://image.tmdb.org/t/p/w500${item.poster_path}`}
              srcSet={`https://image.tmdb.org/t/p/w500${item.poster_path}`}
              alt={item.title}
              loading="lazy"
            />
            <ImageListItemBar
              title={item.title}
              subtitle={item.rating}
              actionIcon={
                <IconButton
                  sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                  aria-label={`info about ${item.title}`}
                >
                  <LocalMoviesIcon />
                </IconButton>
              }
            />
          </ImageListItem>
        ))}
      </ImageList>
    );
  }
}
