//import logo from "./logo.svg";
import * as React from "react";
import Container from "@mui/material/Container";
import { Box } from "@mui/system";
import IntroPage from "./IntroPage";
import QuestionPage from "./QuestionPage";
import ResultsPage from "./ResultsPage";
import QuestionMarkIcon from "@mui/icons-material/QuestionMark";
import TopBar from "./TopBar";
import axios from "axios";

function HandlePage(props) {
  if (props.tree.rule) {
    return (
      <QuestionPage
        tree={props.tree}
        setTree={props.setTree}
        question={props.tree.rule_text}
        icon={<QuestionMarkIcon fontSize="large" />}
      />
    );
  } else {
    return <ResultsPage id={props.tree.movie_id} />;
  }
}

export default class TwentyQ extends React.Component {
  state = { tree: {}, intro: true };
  componentDidMount() {
    console.log(this.props);
    axios.get("/getTree").then((res) => {
      console.log(res["data"]);
      const tree = res["data"];
      this.setState({ tree: tree, intro: true });
    });
    console.log(this.state);
  }

  componentDidUpdate() {
    console.log(this.state);
  }
  render() {
    return (
      <div>
        <Container maxWidth="xl">
          <Box component="main" sx={{ flexGrow: 1, paddingLeft: 8 }}>
            <TopBar />
            {this.state.intro ? (
              <IntroPage
                exitIntro={() => this.setState({ ...this.state, intro: false })}
              />
            ) : (
              <HandlePage
                tree={this.state.tree}
                setTree={(tree) => this.setState({ ...this.state, tree: tree })}
              />
            )}
          </Box>
        </Container>
      </div>
    );
  }
}
