//import logo from "./logo.svg";
import * as React from "react";
import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";
import IconButton from "@mui/material/IconButton";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import Slide from "@mui/material/Slide";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import FavoriteIcon from "@mui/icons-material/Favorite";
import Avatar from "@mui/material/Avatar";
import TextField  from "@mui/material/TextField ";
import SearchIcon from "@mui/icons-material/Search";
import axios from "axios";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default class SelectFavorite extends React.Component {
    state = {
        search: "The Godfather",
        movies:[]
      };

      componentDidMount() {
        console.log(this.props);
        axios.get("/searchByTitle/"+this.state.search).then((res) => {
          console.log(res["data"]["results"]);
          const movies = res["data"]["results"];
          this.setState({search:"", movies:movies});
        });
        console.log(this.state);
      }
  render() {
    const handleClose = () => {
      this.props.setOpen(false);
    };
    const handleSearchChange= (event) => {
        this.state.search= event.target.value
        axios.get("/searchByTitle/"+this.state.search).then((res) => {
          console.log(res["data"]["results"]);
          const movies = res["data"]["results"];
          this.setState({search:this.state.search, movies:movies});
        });
    }

    return (
      <div>
        <Dialog
          open={this.props.open}
          TransitionComponent={Transition}
          keepMounted
          onClose={handleClose}
          aria-describedby="alert-dialog-slide-description"
        >
          <DialogTitle>{"Use Google's location service?"}</DialogTitle>
          <DialogContent>
            <DialogContentText id="alert-dialog-slide-description">
              Let Google help apps determine location. This means sending
              anonymous location data to Google, even when no apps are running.
            </DialogContentText>

            <Paper
              component="form"
              sx={{ p: "2px 4px", display: "flex", alignItems: "center" }}
            >
              <TextField 
                    onSubmit={handleClose}
              onChange={handleSearchChange}
                sx={{ ml: 1, flex: 1 }}
                placeholder="Search Movies"
                inputProps={{ "aria-label": "search movies" }}
              />
                <SearchIcon />
            </Paper>
            <List dense sx={{ width: "100%", bgcolor: "background.paper" }}>
              {this.state.movies.map((item) => {
                return (
                  <ListItem
                    key={item}
                    onClick={handleClose}
                    secondaryAction={
                      <IconButton
                        aria-label={`Select Movie`}
                        onClick={handleClose}
                      >
                        <FavoriteIcon />
                      </IconButton>
                    }
                    disablePadding
                  >
                    <ListItemButton>
                      <ListItemAvatar>
                        <Avatar
                          alt={`https://image.tmdb.org/t/p/w500${item.poster_path}`}
                          src={`https://image.tmdb.org/t/p/w500${item.poster_path}`}
                        />
                      </ListItemAvatar>
                      <ListItemText
                        primary={`${item.title}`}
                      />
                    </ListItemButton>
                  </ListItem>
                );
              })}
            </List>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
          </DialogActions>
        </Dialog>
      </div>
    );
  }
}
