import * as React from "react";
import Typography from "@mui/material/Typography";
import ImageListItem from "@mui/material/ImageListItem";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import IconButton from "@mui/material/IconButton";
import LocalMoviesIcon from "@mui/icons-material/LocalMovies";
import axios from "axios";


export default class ResultsPage extends React.Component {
  state = {}
  componentDidMount() {
    console.log(this.props);
    axios.get("/movie/"+this.props.id).then((res) => {
      console.log(res["data"]);
      const movie = res["data"];
      this.setState(movie);
    });
    console.log(this.state);
  }
  render() {
  return (
    <div>
      <Typography variant="h5">We Recommend</Typography>
      <ImageListItem key={this.state.img} sx={{ width: 500, height: 450 }}>
        <img
          src={`https://image.tmdb.org/t/p/w500${this.state.poster_path}`}
          srcSet={`https://image.tmdb.org/t/p/w500${this.state.poster_path}`}
          alt={this.state.title}
          loading="lazy"
        />
        <ImageListItemBar
          title={this.state.title}
          subtitle={this.state.rating}
          actionIcon={
            <IconButton
              sx={{ color: "rgba(255, 255, 255, 0.54)" }}
              aria-label={`info about ${this.state.title}`}
            >
              <LocalMoviesIcon />
            </IconButton>
          }
        />
      </ImageListItem>
    </div>
  );
}
}
