//import logo from "./logo.svg";
import * as React from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import MovieList from "./MovieList";
import Paper from "@mui/material/Paper";
import FavoriteMovie from "./FavoriteMovie";
import MovieStats from "./MovieStats";
const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));
export default function MovieLists(props) {
  const [open, setOpen] = React.useState(false);

  return (
    <div>
      <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={2}>
        <Box gridColumn="span 3">
          <FavoriteMovie user={props.user} open={open} setOpen={setOpen} />
        </Box>
        <Box gridColumn="span 9">
          <Item>
            <Typography variant="h5" align="left" component="h5">
              {props.user}'s Favorite Movies
            </Typography>
            <MovieList listName={"/getList/love"} />
            <Typography variant="h5" align="left" component="h5">
              {props.user}'s Want to Watch
            </Typography>
            <MovieList listName={"/getList/later"} />
            <Typography variant="h5" align="left" component="h5">
              {props.user} has Watched
            </Typography>
            <MovieList listName={"/getList/watch"} />
            <Typography variant="h5" align="left" component="h5">
              Based on {props.user}'s watch history, we recommend
            </Typography>
            <MovieList listName={"/recommendation"} />
          </Item>
          <br></br>
          <Item>
            <MovieStats user={props.user} />
          </Item>
        </Box>
      </Box>
    </div>
  );
}
