import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Typography from "@mui/material/Typography";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import axios from "axios";

export default class MoviePoster extends React.Component {
  state = {
    watch: false,
    love: false,
    hate: false,
    later: false,
    like: false,
  };
  componentDidUpdate() {
    if(this.props.lists != this.state) {
      console.log(this.state)
      this.setState(this.props.lists);
    }
  }
  render() {
    const handleWatchList = () => {
      if (this.state.watch == true) {
        axios
          .get("/removeFromList/watch/" + this.props.movie.id)
          .then((res) => {
            this.setState({
              watch: false,
              love: this.state.love,
              hate: this.state.hate,
              later: this.state.later,
              like: this.state.like,
            });

            console.log(this.props.lists);
          });
      } else {
        axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: true,
            love: this.state.love,
            hate: this.state.hate,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.state);
        });
      }
    };
    const handleLikeList = () => {
      if (this.state.watch == true) {
        axios
          .get("/removeFromList/watch/" + this.props.movie.id)
          .then((res) => {
            this.setState({
              watch: false,
              love: this.state.love,
              hate: this.state.hate,
              later: this.state.later,
              like: false,
            });

            console.log(this.props.lists);
          });
      } else {
        axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: true,
            love: this.state.love,
            hate: this.state.hate,
            later: this.state.later,
            like: true,
          });
          console.log(this.state);
        });
      }
    };

    const handleLaterList = () => {
      if (this.state.later == true) {
        axios
          .get("/removeFromList/later/" + this.props.movie.id)
          .then((res) => {
            this.setState({
              watch: this.state.watch,
              love: this.state.love,
              hate: this.state.hate,
              later: false,
              like: this.state.like,
            });
            console.log(this.props.lists);
          });
      } else {
        axios.get("/addToList/later/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: this.state.watch,
            love: this.state.love,
            hate: this.state.hate,
            later: true,
            like: this.state.like,
          });
          console.log(this.props.lists);
        });
      }
    };
    const handleLoveList = () => {
      if (this.state.love == true) {
        axios.get("/removeFromList/love/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: this.state.watch,
            love: false,
            hate: this.state.hate,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.props.lists);
        });
      } else {
        axios.get("/addToList/love/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: this.state.watch,
            love: true,
            hate: this.state.hate,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.props.lists);
        });
      }
    };
    const handleHateList = () => {
      if (this.state.hate == true) {
        axios.get("/removeFromList/hate/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: this.state.watch,
            love: this.state.love,
            hate: false,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.props.lists);
        });
      } else {
        axios.get("/addToList/hate/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: this.state.watch,
            love: this.state.love,
            hate: true,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.props.lists);
        });
      }
    };
    return (
      <div>
        <ImageListItem>
          <img
            src={
              this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            srcSet={
              this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            alt={"Image"}
            loading="lazy"
          />
          <ImageListItemBar
            sx={{ background: "rgba(0,0,0,0)" }}
            actionIcon={
              this.props.user !== "logged_out" ? (
                <div>
                  <IconButton
                    sx={
                      this.state.watch
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleWatchList}
                    aria-label={`Seen It`}
                  >
                    <VisibilityIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.later
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLaterList}
                    aria-label={`Watch Later`}
                  >
                    <WatchLaterIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.love
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLoveList}
                    aria-label={`Favorite`}
                  >
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.like
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLikeList}
                    aria-label={`Like It`}
                  >
                    <ThumbUpIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.hate
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleHateList}
                    aria-label={`Hate It`}
                  >
                    <ThumbDownAltIcon />
                  </IconButton>
                </div>
              ) : (
                <></>
              )
            }
          />
        </ImageListItem>
        <Typography variant="h5" align="center" component="h4">
          {this.props.movie.title}
        </Typography>
      </div>
    );
  }
}
