import * as React from 'react';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import { RiNetflixFill } from 'react-icons/ri';
import { SiHulu, SiHbo, SiShowtime, SiAppletv, SiCrunchyroll, SiStarz,SiYoutubetv} from 'react-icons/si';
import {FaAmazon} from 'react-icons/fa'
import {AiFillYoutube} from 'react-icons/ai'

export default function StreamingSelector() {
  const [formats, setFormats] = React.useState(() => ['bold', 'italic']);

  const handleFormat = (event, newFormats) => {
    setFormats(newFormats);
  };

  return (
    <ToggleButtonGroup
        orientation="vertical"
      value={formats}
      onChange={handleFormat}
      aria-label="text formatting"
    >
      <ToggleButton value="Netflix" aria-label="netflix">
         <RiNetflixFill />
      </ToggleButton>
      <ToggleButton value="Hulu" aria-label="hulu">
        <SiHulu/>
      </ToggleButton>
      <ToggleButton value="Amazon" aria-label="amazon">
        <FaAmazon />
      </ToggleButton>
      <ToggleButton value="HBO" aria-label="hbo">
        <SiHbo/>
      </ToggleButton>
      <ToggleButton value="Showtime" aria-label="showtime">
        <SiShowtime/>
      </ToggleButton>
      <ToggleButton value="Apple TV" aria-label="apple-tv">
        <SiAppletv/>
      </ToggleButton>
      <ToggleButton value="Crunchyroll" aria-label="crunchy-roll">
        <SiCrunchyroll/>
      </ToggleButton>
      <ToggleButton value="Starz" aria-label="starz">
        <SiStarz/>
      </ToggleButton>
      <ToggleButton value="youtube" aria-label="youtube">
        <AiFillYoutube/>
      </ToggleButton>
      <ToggleButton value="Youtube TV" aria-label="youtube-tv">
        <SiYoutubetv/>
      </ToggleButton>
    </ToggleButtonGroup>
  );
}
