//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import { styled } from "@mui/material/styles";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import EditIcon from "@mui/icons-material/Edit";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import SelectFavorite from "./SelectFavorite";
import {defaultMovie} from "../MovieDetail/DefaultMovie"
import axios from "axios";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default class FavoriteMovie extends React.Component {
  state = {favorite:defaultMovie}
  componentDidMount() {
    console.log(this.props);
    axios.get("/getFavorite").then((res) => {
      console.log(res["data"]["results"]);
      const movie = res["data"]["results"];
      this.setState({ favorite: movie });
      console.log(this.state);
    });
  }
  componentDidUpdate() {
    console.log(this.props);
    axios.get("/getFavorite").then((res) => {
      if(res["data"]["results"].id != this.state.favorite.id) {
      console.log(res["data"]["results"]);
      const movie = res["data"]["results"];
      this.setState({ favorite: movie });
      console.log(this.state);
      }
    });
  }

  render() {
    const handleClickOpen = () => {
      this.props.setOpen(true);
    };

    return (
      <div>
        <SelectFavorite setOpen={this.props.setOpen} open={this.props.open} />
        <Item>
          <Typography variant="h5" component="h5">
            {this.props.user}'s Favorite Movie
          </Typography>
          <ImageListItem key="favorite">
            <img
              src={this.state.favorite.poster_path?`https://image.tmdb.org/t/p/w500${this.state.favorite.poster_path}`:"https://strykerslev.com/wp-content/uploads/2021/05/noPhoto.png"}
              srcSet={this.state.favorite.poster_path?`https://image.tmdb.org/t/p/w500${this.state.favorite.poster_path}`:"https://strykerslev.com/wp-content/uploads/2021/05/noPhoto.png"}
              alt={"Image"}
              loading="lazy"
            />
            <ImageListItemBar
              sx={{ background: "rgba(0,0,0,0)" }}
              actionIcon={
                <IconButton
                  sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                  aria-label={`Edit Movie`}
                  onClick={handleClickOpen}
                >
                  <EditIcon />
                </IconButton>
              }
            />
          </ImageListItem>
          {this.state.title}
        </Item>
      </div>
    );
  }
}
