//import logo from "./logo.svg";
import * as React from "react";
import Container from "@mui/material/Container";
import TopBar from "./TopBar";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import MovieDetail from "./MovieDetail";
import { styled } from "@mui/material/styles";
import { useParams } from "react-router-dom";
import axios from "axios";
import { defaultMovie } from "./DefaultMovie";
import LoadPage from "../LoadPage";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

const Offset = styled('div')(({ theme }) => theme.mixins.toolbar);


export default function Movie(props) {
  let { id } = useParams();
  return (
    <div>
      <MovieQuery id={id} user={props.user} />
    </div>
  );
}

class MovieQuery extends React.Component {
  state = {
    movie: defaultMovie,
    credit: { cast: [], crew: [] },
    loading: true,
    streaming: [],
    update: false,
    lists: {
      watch: false,
      love: false,
      hate: false,
      like: false,
      later: false,
    },
  };
  componentDidMount() {
    axios.get("/allMovie/" + this.props.id).then((res) => {
      const movie = res["data"]["results"];
      console.log(movie);
      this.setState(movie);
    });
  }

  render() {
    return (
      <div>

      <Container maxWidth="xl">
        <Box component="main" sx={{ flexGrow: 1, paddingLeft: 8 }}>
        <TopBar />
      <Offset />

              <LoadPage open={this.state.movie == defaultMovie} />
              <MovieDetail
                movie={this.state.movie}
                credit={this.state.credit}
                streaming={this.state.streaming}
                user={this.props.user}
                lists={this.state.lists}
                setState={this.setState}
              />
          </Box>
        </Container>
      </div>
    );
  }
}
