import * as React from "react";
import ImageList from "@mui/material/ImageList";
import ImageListItem from "@mui/material/ImageListItem";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import IconButton from "@mui/material/IconButton";
import LocalMoviesIcon from "@mui/icons-material/LocalMovies";
import { genreIds } from "./genreIds";
import { languages } from "./languages";
import MovieButton from "./MovieButton";
import axios from "axios";

function getNumberCols() {
  const { innerWidth: width } = window;
  return Math.floor(width / 250);
}

function getGenreID(genre) {
  for (let i = 0; i < genreIds.length; i++) {
    if (genreIds[i]["name"] === genre) {
      return genreIds[i]["id"];
    }
  }
}

function getLanguageID(lang) {
  for (let i = 0; i < languages.length; i++) {
    if (languages[i]["name"] === lang) {
      return languages[i]["id"];
    }
  }
}

function getSortingMethod(sort) {
  if (sort == 0) {
    return "vote_average";
  } else if ((sort = 1)) {
    return "popularity";
  } else {
    return "primary_release";
  }
}

function makeSearch(props) {
  if (props.search != "") {
    return "/searchByTitle/" + props.search;
  } else {
    let cert = "";
    console.log(props);
    for (let i = 0; i < props.rating.length - 1; i++) {
      cert += props.rating[i] + "|";
    }
    cert += props.rating[props.rating.length - 1];

    let genres = "";
    for (let i = 0; i < props.genreName.length - 1; i++) {
      genres += getGenreID(props.genreName[i]) + "|";
    }
    genres += getGenreID(props.genreName[props.genreName.length - 1]);

    let languages = "";
    for (let i = 0; i < props.languageName.length - 1; i++) {
      languages += getLanguageID(props.languageName[i]) + "|";
    }
    languages += getLanguageID(
      props.languageName[props.languageName.length - 1]
    );

    return (
      "/filters/" +
      cert +
      "/" +
      props.year[0] +
      "/" +
      props.year[1] +
      "/" +
      props.runTime[0] +
      "/" +
      props.runTime[1] +
      "/" +
      genres +
      "/" +
      languages +
      "/" +
      getSortingMethod(props.sortingMethod) +
      "." +
      (props.sortingDirection ? "asc" : "desc")
    );
  }
}

export default class PageContents extends React.Component {
  state = {
    movies: [],
    oldprops: {},
  };

  componentDidMount() {
    console.log(this.props);
    axios.get(makeSearch(this.props)).then((res) => {
      console.log(res["data"]["results"]);
      const movies = res["data"]["results"];
      const oldprops = this.props;
      this.setState({ movies, oldprops });
    });
    console.log(this.state);
  }
  componentDidUpdate() {
    if (this.props != this.state.oldprops) {
      console.log(this.props);
      axios.get(makeSearch(this.props)).then((res) => {
        console.log(res["data"]["results"]);
        const movies = res["data"]["results"];
        const oldprops = this.props;
        this.setState({ movies, oldprops });
      });
      console.log(this.state);
    }
  }

  render() {
    const handleWatchList = () => {
      if (this.state.watch == true) {
        axios
          .get("/removeFromList/watch/" + this.props.movie.id)
          .then((res) => {
            this.setState({
              watch: false,
              love: this.state.love,
              hate: this.state.hate,
              later: this.state.later,
              like: this.state.like,
            });

            console.log(this.props.lists);
          });
      } else {
        axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
          this.setState({
            watch: true,
            love: this.state.love,
            hate: this.state.hate,
            later: this.state.later,
            like: this.state.like,
          });
          console.log(this.state);
        });
      }
    };
    return (
      <ImageList cols={getNumberCols()}>
        {this.state.movies.map(
          (item) =>
            item.poster_path && (
              <ImageListItem key={item.poster_path}>
                <img
                  src={
                    item.poster_path
                      ? `https://image.tmdb.org/t/p/w500${item.poster_path}`
                      : "https://www.digitalartsonline.co.uk/cmsdata/slideshow/3347360/Step2.jpg"
                  }
                  srcSet={
                    item.poster_path
                      ? `https://image.tmdb.org/t/p/w500${item.poster_path}`
                      : "https://www.digitalartsonline.co.uk/cmsdata/slideshow/3347360/Step2.jpg"
                  }
                  alt={item.title}
                  loading="lazy"
                />
                <ImageListItemBar
                  title={item.title}
                  titleWrap={{wordWrap: 'break-word'}}
                  actionPosition="left"
                  sx={{
                    background:
                      'linear-gradient(to bottom, rgba(0,0,0,0.7) 0%, ' +
                      'rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
                  }}
                  subtitle={item.vote_average}
                  position="top"
                  onClick={() => {
                    window.location.href = "/movieDetails/" + item["id"];
                  }}
                  actionIcon={
                    <MovieButton/>
                  }
                />
              </ImageListItem>
            )
        )}
      </ImageList>
    );
  }
}
