//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import ImageList from "@mui/material/ImageList";
import axios from "axios";
import CircularProgress from '@mui/material/CircularProgress';

export default class MovieList extends React.Component {
  state = { movies: false };



  componentDidMount() {
    console.log(this.props);
    axios.get("/getList/" + this.props.listName).then((res) => {
      console.log(res["data"]["results"]);
      const movies = res["data"]["results"];
      this.setState({ movies: movies });
      console.log(this.state);
    });
  }
  render() {
    const getHeight = () => {
      if(this.state.movies.length > 8) {
        return {height:300}
      }
      return {height:"auto"}
    }
    return (
      this.state.movies?<ImageList cols={8} sx={getHeight()}>
        {this.state.movies.map((item) => (
          <ImageListItem key={item.img}>
            <img
              src={`https://image.tmdb.org/t/p/w500${item.poster_path}?w=164&h=164&fit=crop&auto=format`}
              srcSet={`https://image.tmdb.org/t/p/w500${item.poster_path}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
              onClick={() => {
                window.location.href = "/movieDetails/" + item.id;
              }}
              alt={item.title}
              loading="lazy"
            />
          </ImageListItem>
        ))}
      </ImageList>:<CircularProgress />
    );
  }
}
