import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Typography from "@mui/material/Typography";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import axios from "axios";

export default class MoviePoster extends React.Component {
  state = {}
  componentDidMount() {
    axios.get("/lists/" + this.props.id).then((res) => {
      const lists = res["data"];
      console.log(lists);
      this.setState(lists);
    });
  }

  render() {
    const handleWatchList = () => {
      if (this.state.watch == true) {
        axios
          .get("/removeFromList/watch/" + this.props.id)
          .then((res) => {
            this.setState({...this.state,watch:false})
          });
      } else {
        axios.get("/addToList/watch/" + this.props.id).then((res) => {
          this.setState({...this.state,watch:true})

        });
      }
    };
    const handleLikeList = () => {
      if (this.state.like == true) {
        axios
          .get("/removeFromList/like/" + this.props.id)
          .then((res) => {
            this.setState({...this.state,like:false})
          });
      } else {
        axios.get("/addToList/like/" + this.props.id).then((res) => {
          this.setState({...this.state,like:true})

        });
      }
    };

    const handleLaterList = () => {
      if (this.state.later == true) {
        axios
          .get("/removeFromList/later/" + this.props.id)
          .then((res) => {
            this.setState({...this.state,leter:false})
          });
      } else {
        axios.get("/addToList/later/" + this.props.id).then((res) => {
          this.setState({...this.state,leter:true})
        });
      }
    };
    const handleLoveList = () => {
      if (this.state.love == true) {
        axios.get("/removeFromList/love/" + this.props.id).then((res) => {
          this.setState({...this.state,love:false})
        });
      } else {
        axios.get("/addToList/love/" + this.props.id).then((res) => {
          this.setState({...this.state,love:true})
        });
      }
    };
    const handleHateList = () => {
      if (this.state.hate == true) {
        axios.get("/removeFromList/hate/" + this.props.id).then((res) => {
          this.setState({...this.state,hate:false})
        });
      } else {
        axios.get("/addToList/hate/" + this.props.id).then((res) => {
          this.setState({...this.state,hate:true})
        });
      }
    };
    return (
      <div>
        <ImageListItem>
          <img
            src={
              this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            srcSet={
              this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            alt={"Image"}
            loading="lazy"
          />
          <ImageListItemBar
            sx={{ background: "rgba(0,0,0,0)" }}
            actionIcon={
              this.props.user !== "logged_out" ? (
                <div>
                  <IconButton
                    sx={
                      this.state.watch
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleWatchList}
                    aria-label={`Seen It`}
                  >
                    <VisibilityIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.later
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLaterList}
                    aria-label={`Watch Later`}
                  >
                    <WatchLaterIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.love
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLoveList}
                    aria-label={`Favorite`}
                  >
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.like
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLikeList}
                    aria-label={`Like It`}
                  >
                    <ThumbUpIcon />
                  </IconButton>
                  <IconButton
                    sx={
                      this.state.hate
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleHateList}
                    aria-label={`Hate It`}
                  >
                    <ThumbDownAltIcon />
                  </IconButton>
                </div>
              ) : (
                <></>
              )
            }
          />
        </ImageListItem>
        <Typography variant="h5" align="center" component="h4">
          {this.props.movie.title}
        </Typography>
      </div>
    );
  }
}
