//import logo from "./logo.svg";
import * as React from "react";
import Container from "@mui/material/Container";
import TopBar from "./TopBar";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import MovieDetail from "./MovieDetail";
import { styled } from "@mui/material/styles";
import { useParams } from "react-router-dom";
import axios from "axios";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default function Movie(props) {
  let { id } = useParams();
  return (
    <div>
      <MovieQuery id={id} user={props.user} />
    </div>
  );
}

class MovieQuery extends React.Component {
  state = { movie: {}, credit: {cast:[], crew:[]}, streaming: {}};
  componentDidMount() {
    axios.get("/movie/" + this.props.id).then((res) => {
      const movie = res["data"];
      axios.get("/credit/" + this.props.id).then((res) => {
        const credit = res["data"];
        axios.get("/streaming/" + this.props.id).then((res) => {
          const streaming = res["data"];
          if(props.user!="logged_out") {
          axios.get("/isInList/watch/" + this.props.id).then((res) => {
            let watched = res["data"]["results"]
            axios.get("/isInList/love/" + this.props.id).then((res) => {
              let loved = res["data"]["results"]
              axios.get("/isInList/hate/" + this.props.id).then((res) => {
                let hated = res["data"]["results"]
                axios.get("/isInList/later/" + this.props.id).then((res) => {
                  let later = res["data"]["results"]
                  this.setState({ movie: movie, credit: credit, streaming: streaming, lists: {watch:watched,love:loved,hate:hated,later:later}})
                  });
                });
              });
            });
          } else {
            this.setState({ movie: movie, credit: credit, streaming: streaming, lists: {watch:'false',love:'false',hate:'false',later:'false'}})
          }
});
});
    });
  }

  render() {
    const handleClickOpen = () => {};

    return (
      <div>
        <Container maxWidth="xl">
          <Box component="main" sx={{ flexGrow: 1, paddingLeft: 8 }}>
            <TopBar />
            <Box sx={{ width: 1 }}>
              <MovieDetail
                movie={this.state.movie}
                credit={this.state.credit}
                streaming={this.state.streaming}
                user={this.props.user}
                lists={this.state.lists}
              />
            </Box>
          </Box>
        </Container>
      </div>
    );
  }
}
