import * as React from "react";
import ChevronLeftIcon from "@mui/icons-material/ChevronLeft";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import ListItem from "@mui/material/ListItem";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import ListItemButton from '@mui/material/ListItemButton';
import InsertEmoticonIcon from '@mui/icons-material/InsertEmoticon';import Divider from "@mui/material/Divider";
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import List from "@mui/material/List";
import IconButton from "@mui/material/IconButton";
import { styled, useTheme } from "@mui/material/styles";
import MuiDrawer from "@mui/material/Drawer";
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import TranslateIcon from '@mui/icons-material/Translate';
import LiveTvIcon from '@mui/icons-material/LiveTv';
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';

const Drawer = styled(MuiDrawer, {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  width: drawerWidth,
  flexShrink: 0,
  whiteSpace: "nowrap",
  boxSizing: "border-box",
  ...(open && {
    ...openedMixin(theme),
    "& .MuiDrawer-paper": openedMixin(theme),
  }),
  ...(!open && {
    ...closedMixin(theme),
    "& .MuiDrawer-paper": closedMixin(theme),
  }),
}));

const drawerWidth = 240;

const openedMixin = (theme) => ({
  width: drawerWidth,
  transition: theme.transitions.create("width", {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen,
  }),
  overflowX: "hidden",
});

const closedMixin = (theme) => ({
  transition: theme.transitions.create("width", {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  overflowX: "hidden",
  width: `calc(${theme.spacing(7)} + 1px)`,
  [theme.breakpoints.up("sm")]: {
    width: `calc(${theme.spacing(9)} + 1px)`,
  },
});

const DrawerHeader = styled("div")(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  justifyContent: "flex-end",
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
}));

export const mainListItems = (
  <React.Fragment>
    <ListItemButton>
      <ListItemIcon>
        <ThumbUpIcon/>
      </ListItemIcon>
      <ListItemText primary="Critic Score" />
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <InsertEmoticonIcon/>
      </ListItemIcon>
      <ListItemText primary="User Score" />
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <CalendarTodayIcon/>
      </ListItemIcon>
      <ListItemText primary="Release Year"/>
    </ListItemButton>
    <Divider sx={{ my: 1 }} />
    <ListItemButton>
      <ListItemIcon>
        <ThumbUpIcon/>
      </ListItemIcon>
      <ListItemText primary="Genre"/>
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <InsertEmoticonIcon/>
      </ListItemIcon>
      <ListItemText primary="Rating" />
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <CalendarTodayIcon/>
      </ListItemIcon>
      <ListItemText primary="Release Year"/>
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <TranslateIcon/>
      </ListItemIcon>
      <ListItemText primary="Langugae"/>
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <AccessTimeIcon/>
      </ListItemIcon>
      <ListItemText primary="Runtime"/>
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <LiveTvIcon/>
      </ListItemIcon>
      <ListItemText primary="Streaming"/>
    </ListItemButton>
    <ListItemButton>
      <ListItemIcon>
        <RemoveRedEyeIcon/>
      </ListItemIcon>
      <ListItemText primary="Seen it"/>
    </ListItemButton>
  </React.Fragment>
);


function SideMenu(props) {
  const theme = useTheme();

  const handleDrawerClose = () => {
    props.setOpen(false);
  };
  return (
    <Drawer variant="permanent" open={props.open}>
      <DrawerHeader>
        <IconButton onClick={handleDrawerClose}>
          {theme.direction === "rtl" ? (
            <ChevronRightIcon />
          ) : (
            <ChevronLeftIcon />
          )}
        </IconButton>
      </DrawerHeader>
      <List>
        {mainListItems}
      </List>
    </Drawer>
  );
}

export default SideMenu;
