import * as React from 'react';
import Box from '@mui/material/Box';
import SpeedDial from '@mui/material/SpeedDial';
import SpeedDialAction from '@mui/material/SpeedDialAction';
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import LocalMoviesIcon from "@mui/icons-material/LocalMovies";



export default function MovieButton() {
    const actions = [
        { icon: <VisibilityIcon/>, name: 'Seen It', handle: handleWatchList },
        { icon: <WatchLaterIcon />, name: 'Watch Later', handle: handleLaterList },
        { icon: <FavoriteIcon />, name: 'Love', handle: handleLoveList },
      ];
    const handleWatchList = () => {
        if (this.state.watch == true) {
          axios
            .get("/removeFromList/watch/" + this.props.movie.id)
            .then((res) => {
              this.setState({
                watch: false,
                love: this.state.love,
                hate: this.state.hate,
                later: this.state.later,
                like: this.state.like,
              });

              console.log(this.props.lists);
            });
        } else {
          axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
            this.setState({
              watch: true,
              love: this.state.love,
              hate: this.state.hate,
              later: this.state.later,
              like: this.state.like,
            });
            console.log(this.state);
          });
        }
      };

      const handleLaterList = () => {
        if (this.state.later == true) {
          axios
            .get("/removeFromList/later/" + this.props.movie.id)
            .then((res) => {
              this.setState({
                watch: this.state.watch,
                love: this.state.love,
                hate: this.state.hate,
                later: false,
                like: this.state.like,
              });
              console.log(this.props.lists);
            });
        } else {
          axios.get("/addToList/later/" + this.props.movie.id).then((res) => {
            this.setState({
              watch: this.state.watch,
              love: this.state.love,
              hate: this.state.hate,
              later: true,
              like: this.state.like,
            });
            console.log(this.props.lists);
          });
        }
      };

      const handleLoveList = () => {
        if (this.state.love == true) {
          axios.get("/removeFromList/love/" + this.props.movie.id).then((res) => {
            this.setState({
              watch: this.state.watch,
              love: false,
              hate: this.state.hate,
              later: this.state.later,
              like: this.state.like,
            });
            console.log(this.props.lists);
          });
        } else {
          axios.get("/addToList/love/" + this.props.movie.id).then((res) => {
            this.setState({
              watch: this.state.watch,
              love: true,
              hate: this.state.hate,
              later: this.state.later,
              like: this.state.like,
            });
            console.log(this.props.lists);
          });
        }
      };

  return (
      <SpeedDial
