import * as React from "react";
import Box from "@mui/material/Box";
import Slider from "@mui/material/Slider";
import Grid from "@mui/material/Grid";

function valuetext(value) {
  return `${value}`;
}

export function RunTimeSlider(props) {
  const handleChange = (event, newValue) => {
    props.setRunTime(newValue);
  };
  const marks = [
    {
      value: 0,
      label: "0",
    },
    {
      value: 60,
      label: "1 hr",
    },
    {
      value: 120,
      label: "2 hr",
    },
    {
      value: 180,
      label: "3 hr",
    },
    {
      value: 240,
      label: "4 hr",
    },
    {
      value: 300,
      label: "5 hr",
    },
  ];
  return (
    <Grid container justifyContent="center" spacing={2}>
      <Box sx={{ width: 175 }}>
        <Slider
          getAriaLabel={() => "Year range"}
          value={props.runTime}
          onChange={handleChange}
          valueLabelDisplay="auto"
          getAriaValueText={valuetext}
          size="small"
          min={0}
          max={300}
          marks={marks}
        />
      </Box>
    </Grid>
  );
}

export function YearSlider(props) {
  const handleChange = (event, newValue) => {
    props.setYear(newValue);
  };
  const marks = [
    {
      value: 1888,
      label: "1888",
    },
    {
      value: new Date().getUTCFullYear(),
      label: "" + new Date().getUTCFullYear(),
    },
  ];
  return (
    <Grid container justifyContent="center" spacing={2}>
      <Box sx={{ width: 175 }}>
        <Slider
          getAriaLabel={() => "Year range"}
          value={props.year}
          onChange={handleChange}
          valueLabelDisplay="auto"
          getAriaValueText={valuetext}
          size="small"
          min={1888}
          max={new Date().getUTCFullYear()}
          marks={marks}
        />
      </Box>
    </Grid>
  );
}
