//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableRow from "@mui/material/TableRow";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import Grid from "@mui/material/Grid";

function createData(name, value) {
  return { name, value };
}

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
}));

function getLanguages(languages) {
  let languagesText = "";
  try {
    for (var i = 0; i < languages.length - 1; i++) {
      languagesText += languages[i].english_name + ", ";
    }
    if (languages.length != 0) {
      languagesText += languages[languages.length - 1].english_name;
    }
  } catch {
    return "";
  }
  return languagesText;
}

function getGenres(genres) {
  let genresText = "";
  try {
    for (var i = 0; i < genres.length - 1; i++) {
      genresText += genres[i].name + ", ";
    }
    if (genres.length != 0) {
      genresText += genres[genres.length - 1].name;
    }
  } catch {
    return "";
  }
  return genresText;
}
var formatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",

  // These options are needed to round to whole numbers if that's what you want.
  //minimumFractionDigits: 0, // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
  //maximumFractionDigits: 0, // (causes 2500.99 to be printed as $2,501)
});

export default function MovieDetail(props) {
  const handleClickOpen = () => {};
  const rows = [
    createData("Tagline", props.movie.tagline),
    createData("Genres", getGenres(props.movie.genres)),
    createData("Budget", formatter.format(props.movie.budget)),
    createData("Popularity", props.movie.popularity),
    createData("Revenue", formatter.format(props.movie.revenue)),
    createData("Release Date", props.movie.release_date),
    createData("Language", getLanguages(props.movie.spoken_languages)),
    createData("Status", props.movie.status),
  ];
  return (
    <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={1}>
      <Box gridColumn="span 4">
        <Item>
          <ImageListItem key="favorite">
            <img
              src={`https://image.tmdb.org/t/p/w500${props.movie.poster_path}`}
              srcSet={`https://image.tmdb.org/t/p/w500${props.movie.poster_path}`}
              alt={"Image"}
              loading="lazy"
            />
            <ImageListItemBar
              sx={{ background: "rgba(0,0,0,0)" }}
              actionIcon={
                <div>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                  >
                    <VisibilityIcon />
                  </IconButton>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                  >
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton
                    sx={{ color: "rgba(255, 255, 255, 0.54)" }}
                    aria-label={`Edit Movie`}
                  >
                    <ThumbDownAltIcon />
                  </IconButton>
                </div>
              }
            />
          </ImageListItem>
          <Typography variant="h5" align="center" component="h4">
            {props.movie.title}
          </Typography>
        </Item>
      </Box>
      <Box gridColumn="span 8" sx={{
                    maxHeight: 600,
                    overflow: "auto",
                  }}>
        <Item>
          <Grid container spacing={1}>
            <Grid item xs={12}>
                <Typography
                  variant="body2"
                  gutterBottom
                  component="h5"
                >
                  {props.movie.overview}
                </Typography>

              <TableContainer component={Paper}>
                <Table aria-label="simple table">
                  <TableBody>
                    {rows.map((row) => (
                      <TableRow
                        key={row.name}
                        sx={{
                          "&:last-child td, &:last-child th": { border: 0 },
                        }}
                      >
                        <TableCell component="th" scope="row">
                          {row.name}
                        </TableCell>
                        <TableCell align="right">{row.value}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="h6" align="center" component="h4">
                Cast
              </Typography>
              {props.credit ? (
                <List
                  dense
                  sx={{
                    maxHeight: 250,
                    overflow: "auto",
                    bgcolor: "background.paper",
                  }}
                >
                  {props.credit.cast.map((item) => {
                    return (
                      <ListItem
                        key={item}
                        disablePadding
                        onClick={() => {
                          window.location.href =
                            "https://www.themoviedb.org/person/" + item.id;
                        }}
                      >
                        <ListItemButton>
                          <ListItemAvatar>
                            <Avatar
                              alt={`https://image.tmdb.org/t/p/w500${item.profile_path}`}
                              src={`https://image.tmdb.org/t/p/w500${item.profile_path}`}
                            />
                          </ListItemAvatar>
                          <ListItemText
                            primary={`${item.name} as ${item.character}`}
                          />
                        </ListItemButton>
                      </ListItem>
                    );
                  })}

                </List>

              ) : (
                <></>
              )}

            </Grid>
            <Grid item xs={6}>
              <Typography variant="h6" align="center" component="h4">
                Crew
              </Typography>
              {props.credit ? (
                <List
                  dense
                  sx={{
                    maxHeight: 250,
                    overflow: "auto",
                    bgcolor: "background.paper",
                  }}
                >
                  {props.credit.crew.map((item) => {
                    return (
                      <ListItem
                        key={item}
                        disablePadding
                        onClick={() => {
                          window.location.href =
                            "https://www.themoviedb.org/person/" + item.id;
                        }}
                      >
                        <ListItemButton>
                          <ListItemAvatar>
                            <Avatar
                              alt={`https://image.tmdb.org/t/p/w500${item.profile_path}`}
                              src={`https://image.tmdb.org/t/p/w500${item.profile_path}`}
                            />
                          </ListItemAvatar>
                          <ListItemText
                            primary={`${item.name} as ${item.job}`}
                          />
                        </ListItemButton>
                      </ListItem>
                    );
                  })}

                </List>

              ) : (
                <></>
              )}

            </Grid>
          </Grid>
        </Item>
      </Box>
    </Box>
  );
}
