import * as React from "react";
import ToggleButton from "@mui/material/ToggleButton";
import ToggleButtonGroup from "@mui/material/ToggleButtonGroup";
import { RiNetflixFill } from "react-icons/ri";
import {
  SiHulu,
  SiHbo,
  SiShowtime,
  SiAppletv,
  SiCrunchyroll,
  SiStarz,
} from "react-icons/si";
import { FaAmazon } from "react-icons/fa";
import { AiFillYoutube } from "react-icons/ai";

export function StreamingSelector(props) {
  const handleStreamingService = (event, newFormats) => {
    props.setStreamingService(newFormats);
  };

  return (
    <ToggleButtonGroup
      orientation="vertical"
      value={props.streamingService}
      onChange={handleStreamingService}
      aria-label="text formatting"
    >
      <ToggleButton value="Netflix" aria-label="netflix">
        <RiNetflixFill />
      </ToggleButton>
      <ToggleButton value="Hulu" aria-label="hulu">
        <SiHulu />
      </ToggleButton>
      <ToggleButton value="Amazon Prime Video" aria-label="amazon">
        <FaAmazon />
      </ToggleButton>
      <ToggleButton value="HBO Max" aria-label="hbo">
        <SiHbo />
      </ToggleButton>
      <ToggleButton value="Showtime" aria-label="showtime">
        <SiShowtime />
      </ToggleButton>
      <ToggleButton value="Apple TV Plus" aria-label="apple-tv">
        <SiAppletv />
      </ToggleButton>
      <ToggleButton value="Crunchyroll" aria-label="crunchy-roll">
        <SiCrunchyroll />
      </ToggleButton>
      <ToggleButton value="Starz" aria-label="starz">
        <SiStarz />
      </ToggleButton>
      <ToggleButton value="YouTube" aria-label="youtube">
        <AiFillYoutube />
      </ToggleButton>
    </ToggleButtonGroup>
  );
}

