//import logo from "./logo.svg";
import * as React from "react";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import { styled } from "@mui/material/styles";
import MuiAppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Badge from "@mui/material/Badge";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import Link from '@mui/material/Link';
import { useNavigate } from "react-router-dom";
const drawerWidth = 240;

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  zIndex: theme.zIndex.drawer + 1,
  transition: theme.transitions.create(["width", "margin"], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

function TopBar(props) {
  const handleDrawerOpen = () => {
    props.setOpen(true);
  };
  const redirect = () => {
    useNavigate("/account");
  }
  return (
    <AppBar position="fixed" open={props.open}>
      <Toolbar
        sx={{
          pr: "24px", // keep right padding when drawer closed
        }}
      >
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          sx={{
            marginRight: "36px",
            ...(props.open && { display: "none" }),
          }}
        >
          <MenuIcon />
        </IconButton>
        <Grid
          container
          direction="row"
          justifyContent="space-between"
          alignItems="center"
        >
          <Link
            variant="h6"
            color="inherit"
            align="right"
            href="/"
            underline="none"
            noWrap
          >
            Movie Recommender
          </Link>
          <div>
            <Button sx={{ margin: 1 }} href="/movieList" color="secondary" variant="contained">Movie Filters</Button>
            <Button sx={{ margin: 1 }} href="/twentyQ" color="secondary" variant="contained">Twenty Questions</Button>

          <IconButton color="inherit" 
          onClick={handleDrawerOpen} sx={{ justifyContent: 1 }}>
            <Badge color="secondary" >
              <AccountCircleIcon />
            </Badge>
          </IconButton>
          </div>
        </Grid>
      </Toolbar>
    </AppBar>
  );
}

export default TopBar;
