import * as React from "react";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import ListItemButton from "@mui/material/ListItemButton";
import InsertEmoticonIcon from "@mui/icons-material/InsertEmoticon";
import Divider from "@mui/material/Divider";
import CalendarTodayIcon from "@mui/icons-material/CalendarToday";
import TranslateIcon from "@mui/icons-material/Translate";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import ExplicitIcon from "@mui/icons-material/Explicit";
import { YearSlider, RunTimeSlider } from "./Sliders";
import RatingSelect from "./RatingSelect";
import { StreamItToggle, SeenItToggle } from "./Toggles";
import GenreSelect from "./GenreSelect";
import LanguageSelect from "./LanguageSelect";
import {
  StreamingSelector,
  StreamingSelector1,
  StreamingSelector2,
} from "./StreamingSelector";
import { styled } from "@mui/material/styles";
import Grid from "@mui/material/Grid";
import ArrowDropUpIcon from "@mui/icons-material/ArrowDropUp";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";
import { SiRottentomatoes } from "react-icons/si";
import TheaterComedyIcon from "@mui/icons-material/TheaterComedy";
const Div = styled("div")(({ theme }) => ({
  ...theme.typography.button,
  backgroundColor: theme.palette.background.paper,
  padding: theme.spacing(1),
}));

function SideMenuItems(props) {
  const [yearOpen, setYearOpen] = React.useState(false);
  const [ratingOpen, setRatingOpen] = React.useState(false);
  const [genreOpen, setGenreOpen] = React.useState(false);
  const [languageOpen, setLanguageOpen] = React.useState(false);
  const [runtimeOpen, setRuntimeOpen] = React.useState(false);
  const [seenItOpen, setSeenItOpen] = React.useState(false);
  const [sortingMethod, setSortingMethod] = React.useState(0);
  const [sortingDirection, setSortingDirection] = React.useState(true);

  const [genreName, setGenreName] = React.useState([]);
  const [languageName, setLanguageName] = React.useState([]);
  const [rating, setRating] = React.useState(() => [
    "G",
    "PG",
    "PG-13",
    "R",
    "NC-17",
    "NR",
  ]);
  const [year, setYear] = React.useState([1888, new Date().getUTCFullYear()]);
  const [runTime, setRunTime] = React.useState([75, 180]);
  const [streamingService, setStreamingService] = React.useState(() => ["Netflix"]);
  const [streamIt, setStreamIt] = React.useState(false);
  const [seenIt, setSeenIt] = React.useState(false);
  return (
    <React.Fragment>
      <Div>{"Sorting"}</Div>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (sortingMethod === 0) {
            setSortingDirection(!sortingDirection);
          } else {
            setSortingDirection(true);
            setSortingMethod(0);
          }
        }}
        selected={sortingMethod === 0 ? true : false}
      >
        <ListItemIcon>
          <SiRottentomatoes />
        </ListItemIcon>
        <ListItemText primary="Critic Score" />
        {sortingMethod === 0 ? (
          sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (sortingMethod === 1) {
            setSortingDirection(!sortingDirection);
          } else {
            setSortingDirection(true);
            setSortingMethod(1);
          }
        }}
        selected={sortingMethod === 1 ? true : false}
      >
        <ListItemIcon>
          <InsertEmoticonIcon />
        </ListItemIcon>
        <ListItemText primary="User Score" />
        {sortingMethod === 1 ? (
          sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (sortingMethod === 2) {
            setSortingDirection(!sortingDirection);
          } else {
            setSortingDirection(true);
            setSortingMethod(2);
          }
        }}
        selected={sortingMethod === 2 ? true : false}
      >
        <ListItemIcon>
          <CalendarTodayIcon />
        </ListItemIcon>
        <ListItemText primary="Release Year" />
        {sortingMethod === 2 ? (
          sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <Divider sx={{ my: 1 }} />
      <Div>{"Filters"}</Div>
      <ListItemButton
        onClick={(event) => {
          setGenreOpen(!genreOpen);
          props.setOpen(true);
        }}
        selected={genreOpen}
      >
        <ListItemIcon>
          <TheaterComedyIcon />
        </ListItemIcon>
        <ListItemText primary="Genre" />
      </ListItemButton>
      {props.open && genreOpen ? <GenreSelect genreName={genreName} setGenreName={setGenreName}/> : <></>}
      <ListItemButton
        onClick={(event) => {
          setRatingOpen(!ratingOpen);
          props.setOpen(true);
        }}
        selected={ratingOpen}
      >
        <ListItemIcon>
          <ExplicitIcon />
        </ListItemIcon>
        <ListItemText primary="Rating" />
      </ListItemButton>
      {props.open && ratingOpen ? <RatingSelect rating={rating} setRating={setRating}/> : <></>}
      <ListItemButton
        onClick={(event) => {
          setYearOpen(!yearOpen);
          props.setOpen(true);
        }}
        selected={yearOpen}
      >
        <ListItemIcon>
          <CalendarTodayIcon />
        </ListItemIcon>
        <ListItemText primary="Release Year" />
      </ListItemButton>
      {props.open && yearOpen ? <YearSlider year={year} setYear={setYear}/> : <></>}

      <ListItemButton
        onClick={(event) => {
          setLanguageOpen(!languageOpen);
          props.setOpen(true);
        }}
        selected={languageOpen}
      >
        {" "}
        <ListItemIcon>
          <TranslateIcon />
        </ListItemIcon>
        <ListItemText primary="Langugae" />
      </ListItemButton>
      {props.open && languageOpen ? <LanguageSelect languageName={languageName} setLanguageName={setLanguageName}/> : <></>}
      <ListItemButton
        onClick={(event) => {
          setRuntimeOpen(!runtimeOpen);
          props.setOpen(true);
        }}
        selected={runtimeOpen}
      >
        {" "}
        <ListItemIcon>
          <AccessTimeIcon />
        </ListItemIcon>
        <ListItemText primary="Runtime" />
      </ListItemButton>
      {props.open && runtimeOpen ? <RunTimeSlider runTime={runTime} setRunTime={setRunTime}/> : <></>}
      <ListItemButton
        onClick={(event) => {
          setSeenItOpen(!seenItOpen);
          props.setOpen(true);
        }}
        selected={seenItOpen}
      >
        {" "}
        <ListItemIcon>
          <RemoveRedEyeIcon />
        </ListItemIcon>
        <ListItemText primary="Seen it" />
      </ListItemButton>
      {props.open && seenItOpen ? <SeenItToggle seenIt={seenIt} setSeenIt={setSeenIt}/> : <></>}
      <Divider sx={{ my: 1 }} />
      {props.open ? (
        <>
          <Div>{"Streaming Services"}</Div>
          <StreamItToggle streamIt={streamIt} setStreamIt={setStreamIt}/>
        </>
      ) : (
        <></>
      )}
      {props.open ? (
        <Grid container>
          <Grid item xs={4}>
            <StreamingSelector streamingService={streamingService} setStreamingService={setStreamingService}/>
          </Grid>
          <Grid item xs={4}>
            <StreamingSelector1 streamingService={streamingService} setStreamingService={setStreamingService}/>
          </Grid>
          <Grid item xs={4}>
            <StreamingSelector2 streamingService={streamingService} setStreamingService={setStreamingService}/>
          </Grid>
        </Grid>
      ) : (
        <StreamingSelector streamingService={streamingService} setStreamingService={setStreamingService}/>
      )}
    </React.Fragment>
  );
}

export default SideMenuItems;
