import * as React from 'react';
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableRow from "@mui/material/TableRow";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import Grid from "@mui/material/Grid";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import axios from "axios";
import {handleWatchList, handleLaterList, handleLoveList, handleHateList, handleLikeList} from "./HandleWatchList"

export default class MoviePoster extends React.Component {
    state = { watch: false, love: false, hate: false, later: false, like: false };
  componentDidMount() {
    this.setState(this.props.lists);
  }
        render() {
        return (
        <div>
            <ImageListItem>
            <img
            src={
                this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            srcSet={
                this.props.movie.poster_path
                ? `https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`
                : "https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"
            }
            alt={"Image"}
            loading="lazy"
            />
            <ImageListItemBar
            sx={{ background: "rgba(0,0,0,0)" }}
            actionIcon={
                this.props.user !== "logged_out" ? (
                <div>
                    <IconButton
                    sx={
                        this.state.watch
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleWatchList}
                    aria-label={`Seen It`}
                    >
                    <VisibilityIcon />
                    </IconButton>
                    <IconButton
                    sx={
                        this.state.later
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLaterList}
                    aria-label={`Watch Later`}
                    >
                    <WatchLaterIcon />
                    </IconButton>
                    <IconButton
                    sx={
                        this.state.love
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLoveList}
                    aria-label={`Favorite`}
                    >
                    <FavoriteIcon />
                    </IconButton>
                    <IconButton
                    sx={
                        this.state.like
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleLikeList}
                    aria-label={`Like It`}
                    >
                    <ThumbUpIcon />
                    </IconButton>
                    <IconButton
                    sx={
                        this.state.hate
                        ? { color: "rgba(255, 255, 255, 1)" }
                        : { color: "rgba(255, 255, 255, 0.54)" }
                    }
                    onClick={handleHateList}
                    aria-label={`Hate It`}
                    >
                    <ThumbDownAltIcon />
                    </IconButton>
                </div>
                ) : (
                <></>
                )
            }
            />
        </ImageListItem>
        <Typography variant="h5" align="center" component="h4">
            {this.props.movie.title}
        </Typography>
      </div>
    )
        }
  }
