//import logo from "./logo.svg";
import * as React from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import MovieList from "./MovieList";
import Paper from "@mui/material/Paper";
import {itemData} from "./itemData"
import FavoriteMovie from "./FavoriteMovie"

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default class MovieLists extends React.Component {


    render() {
      return (
        <div>
        <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={2}>
          <Box gridColumn="span 3">
            <FavoriteMovie open={open} setOpen={setOpen}/>
          </Box>
          <Box gridColumn="span 9">
            <Item>
              <Typography variant="h5" align='left' component="h5">
                {this.props.accountName}'s Favorite Movies
              </Typography>
              <MovieList itemData={itemData.slice(0, 8)}/>
              <Typography variant="h5" align='left' component="h5">
                {this.props.accountName}'s Want to Watch
              </Typography>
              <MovieList itemData={itemData.slice(9, 17)}/>
              <Typography variant="h5" align='left' component="h5">
                {this.props.accountName} has Watched
              </Typography>
              <MovieList itemData={itemData.slice(18, 26)}/>
              </Item>
          <br></br>
            <Item>
              <Typography variant="h5" align='left' component="h5">
                {this.props.accountName}'s Movie Stats
              </Typography>
              Seinfeld
            </Item>
        </Box>
        </Box>
    </div>
      );
    }
  }
