        axios.get("/movie/" + this.props.id).then((res) => {
          console.log(res["data"]["results"]);
          const movie = res["data"]["results"];
          this.setState({movie: movie });
        });
        console.log(this.state);
      }

      render() {
        const handleClickOpen = () => {
        };


        return (
      <div>
        <Container maxWidth="xl">
          <Box component="main" sx={{ flexGrow: 1, paddingLeft: 8 }}>
            <TopBar />
            <Box sx={{ width: 1 }}>
              <MovieDetail />
              {this.props.id}
            </Box>
          </Box>
        </Container>
      </div>
    );
  }

}
