import * as React from "react";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import ListItemButton from "@mui/material/ListItemButton";
import InsertEmoticonIcon from "@mui/icons-material/InsertEmoticon";
import Divider from "@mui/material/Divider";
import CalendarTodayIcon from "@mui/icons-material/CalendarToday";
import TranslateIcon from "@mui/icons-material/Translate";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import ExplicitIcon from "@mui/icons-material/Explicit";
import { YearSlider, RunTimeSlider } from "./Sliders";
import RatingSelect from "./RatingSelect";
import { StreamItToggle, SeenItToggle } from "./Toggles";
import GenreSelect from "./GenreSelect";
import LanguageSelect from "./LanguageSelect";
import {
  StreamingSelector,
  StreamingSelector1,
  StreamingSelector2,
} from "./StreamingSelector";
import { styled } from "@mui/material/styles";
import Grid from "@mui/material/Grid";
import ArrowDropUpIcon from "@mui/icons-material/ArrowDropUp";
import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";
import RecommendIcon from "@mui/icons-material/Recommend";
import TheaterComedyIcon from "@mui/icons-material/TheaterComedy";
const Div = styled("div")(({ theme }) => ({
  ...theme.typography.button,
  backgroundColor: theme.palette.background.paper,
  padding: theme.spacing(1),
}));

function SideMenuItems(props) {
  const [yearOpen, setYearOpen] = React.useState(false);
  const [ratingOpen, setRatingOpen] = React.useState(false);
  const [genreOpen, setGenreOpen] = React.useState(false);
  const [languageOpen, setLanguageOpen] = React.useState(false);
  const [runtimeOpen, setRuntimeOpen] = React.useState(false);
  const [seenItOpen, setSeenItOpen] = React.useState(false);

  return (
    <React.Fragment>
      <Div>{"Sorting"}</Div>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (props.sortingMethod === 0) {
            props.setSortingDirection(!props.sortingDirection);
          } else {
            props.setSortingDirection(false);
            props.setSortingMethod(0);
          }
        }}
        selected={props.sortingMethod === 0 ? true : false}
      >
        <ListItemIcon>
          <RecommendIcon />
        </ListItemIcon>
        <ListItemText primary="User Score" />
        {props.sortingMethod === 0 ? (
          props.sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (props.sortingMethod === 1) {
            props.setSortingDirection(!props.sortingDirection);
          } else {
            props.setSortingDirection(false);
            props.setSortingMethod(1);
          }
        }}
        selected={props.sortingMethod === 1 ? true : false}
      >
        <ListItemIcon>
          <InsertEmoticonIcon />
        </ListItemIcon>
        <ListItemText primary="Popularity" />
        {props.sortingMethod === 1 ? (
          props.sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <ListItemButton
        onClick={(event) => {
          props.setOpen(true);
          if (props.sortingMethod === 2) {
            props.setSortingDirection(!props.sortingDirection);
          } else {
            props.setSortingDirection(false);
            props.setSortingMethod(2);
          }
        }}
        selected={props.sortingMethod === 2 ? true : false}
      >
        <ListItemIcon>
          <CalendarTodayIcon />
        </ListItemIcon>
        <ListItemText primary="Release Year" />
        {props.sortingMethod === 2 ? (
          props.sortingDirection ? (
            <ArrowDropUpIcon />
          ) : (
            <ArrowDropDownIcon />
          )
        ) : (
          <></>
        )}
      </ListItemButton>
      <Divider sx={{ my: 1 }} />
      <Div>{"Filters"}</Div>
      <ListItemButton
        onClick={(event) => {
          setGenreOpen(!genreOpen);
          props.setOpen(true);
        }}
        selected={genreOpen}
      >
        <ListItemIcon>
          <TheaterComedyIcon />
        </ListItemIcon>
        <ListItemText primary="Genre" />
      </ListItemButton>
      {props.open && genreOpen ? (
        <GenreSelect
          genreName={props.genreName}
          setGenreName={props.setGenreName}
        />
      ) : (
        <></>
      )}
      <ListItemButton
        onClick={(event) => {
          setRatingOpen(!ratingOpen);
          props.setOpen(true);
        }}
        selected={ratingOpen}
      >
        <ListItemIcon>
          <ExplicitIcon />
        </ListItemIcon>
        <ListItemText primary="Rating" />
      </ListItemButton>
      {props.open && ratingOpen ? (
        <RatingSelect rating={props.rating} setRating={props.setRating} />
      ) : (
        <></>
      )}
      <ListItemButton
        onClick={(event) => {
          setYearOpen(!yearOpen);
          props.setOpen(true);
        }}
        selected={yearOpen}
      >
        <ListItemIcon>
          <CalendarTodayIcon />
        </ListItemIcon>
        <ListItemText primary="Release Year" />
      </ListItemButton>
      {props.open && yearOpen ? (
        <YearSlider year={props.year} setYear={props.setYear} />
      ) : (
        <></>
      )}

      <ListItemButton
        onClick={(event) => {
          setLanguageOpen(!languageOpen);
          props.setOpen(true);
        }}
        selected={languageOpen}
      >
        {" "}
        <ListItemIcon>
          <TranslateIcon />
        </ListItemIcon>
        <ListItemText primary="Langugae" />
      </ListItemButton>
      {props.open && languageOpen ? (
        <LanguageSelect
          languageName={props.languageName}
          setLanguageName={props.setLanguageName}
        />
      ) : (
        <></>
      )}
      <ListItemButton
        onClick={(event) => {
          setRuntimeOpen(!runtimeOpen);
          props.setOpen(true);
        }}
        selected={runtimeOpen}
      >
        {" "}
        <ListItemIcon>
          <AccessTimeIcon />
        </ListItemIcon>
        <ListItemText primary="Runtime" />
      </ListItemButton>
      {props.open && runtimeOpen ? (
        <RunTimeSlider runTime={props.runTime} setRunTime={props.setRunTime} />
      ) : (
        <></>
      )}
      <ListItemButton
        onClick={(event) => {
          setSeenItOpen(!seenItOpen);
          props.setOpen(true);
        }}
        selected={seenItOpen}
      >
        {" "}
        <ListItemIcon>
          <RemoveRedEyeIcon />
        </ListItemIcon>
        <ListItemText primary="Seen it" />
      </ListItemButton>
      {props.open && seenItOpen ? (
        <SeenItToggle seenIt={props.seenIt} setSeenIt={props.setSeenIt} />
      ) : (
        <></>
      )}
      <Divider sx={{ my: 1 }} />
      {props.open ? (
        <>
          <Div>{"Streaming Services"}</Div>
          <StreamItToggle
            streamIt={props.streamIt}
            setStreamIt={props.setStreamIt}
          />
        </>
      ) : (
        <></>
      )}
      {props.open ? (
        <Grid container>
          <Grid item xs={4}>
            <StreamingSelector
              streamingService={props.streamingService}
              setStreamingService={props.setStreamingService}
            />
          </Grid>
          <Grid item xs={4}>
            <StreamingSelector1
              streamingService={props.streamingService}
              setStreamingService={props.setStreamingService}
            />
          </Grid>
          <Grid item xs={4}>
            <StreamingSelector2
              streamingService={props.streamingService}
              setStreamingService={props.setStreamingService}
            />
          </Grid>
        </Grid>
      ) : (
        <StreamingSelector
          streamingService={props.streamingService}
          setStreamingService={props.setStreamingService}
        />
      )}
    </React.Fragment>
  );
}

export default SideMenuItems;
