//import logo from "./logo.svg";
import * as React from "react";
import ImageListItem from "@mui/material/ImageListItem";
import IconButton from "@mui/material/IconButton";
import ImageListItemBar from "@mui/material/ImageListItemBar";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Paper from "@mui/material/Paper";
import VisibilityIcon from "@mui/icons-material/Visibility";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ThumbDownAltIcon from "@mui/icons-material/ThumbDownAlt";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableRow from "@mui/material/TableRow";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Avatar from "@mui/material/Avatar";
import Grid from "@mui/material/Grid";
import WatchLaterIcon from "@mui/icons-material/WatchLater";
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import axios from "axios";

function createData(name, value) {
  return { name, value };
}

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
}));



function getLanguages(languages) {
  let languagesText = "";
  try {
    for (var i = 0; i < languages.length - 1; i++) {
      languagesText += languages[i].english_name + ", ";
    }
    if (languages.length != 0) {
      languagesText += languages[languages.length - 1].english_name;
    }
  } catch {
    return "";
  }
  return languagesText;
}

function getGenres(genres) {
  let genresText = "";
  try {
    for (var i = 0; i < genres.length - 1; i++) {
      genresText += genres[i].name + ", ";
    }
    if (genres.length != 0) {
      genresText += genres[genres.length - 1].name;
    }
  } catch {
    return "";
  }
  return genresText;
}

function getstreaming(streaming) {
  try {
    console.log(streaming.US)
    console.log(streaming.US.flatrate)
    return streaming.US.flatrate
  } catch (error) {
    console.log(error)
    return []
  }
}

var formatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",

  // These options are needed to round to whole numbers if that's what you want.
  //minimumFractionDigits: 0, // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
  //maximumFractionDigits: 0, // (causes 2500.99 to be printed as $2,501)
});
export default class MovieDetail extends React.Component {
  state= {watch:false,love:false,hate:false,later:false,like:false}
  componentDidMount() {
    this.setState(this.props.lists)
  }
  render() {
  const handleWatchList = () => { 
      if(this.state.watch == true) {
        axios.get("/removeFromList/watch/" + this.props.movie.id).then((res) => {
          this.setState({watch:false,love:this.state.love,hate:this.state.hate,later:this.state.later,like:this.state.like})

          console.log(this.props.lists)
        });
      } else {
        axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
          this.setState({watch:true,love:this.state.love,hate:this.state.hate,later:this.state.later,like:this.state.like})
          console.log(this.state)
        });
      }
  }
  const handleLikeList = () => { 
    if(this.state.watch == true) {
      axios.get("/removeFromList/watch/" + this.props.movie.id).then((res) => {
        this.setState({watch:false,love:this.state.love,hate:this.state.hate,later:this.state.later,like:false})

        console.log(this.props.lists)
      });
    } else {
      axios.get("/addToList/watch/" + this.props.movie.id).then((res) => {
        this.setState({watch:true,love:this.state.love,hate:this.state.hate,later:this.state.later,like:true})
        console.log(this.state)
      });
    }
}
  const handleLaterList = () => { 
    if(this.state.later == true) {
      axios.get("/removeFromList/later/" + this.props.movie.id).then((res) => {
        this.setState({watch:this.state.watch,love:this.state.love,hate:this.state.hate,later:false,like:this.state.like})
        console.log(this.props.lists)
      });
    } else {
      axios.get("/addToList/later/" + this.props.movie.id).then((res) => {
        this.setState({watch:this.state.watch,love:this.state.love,hate:this.state.hate,later:true,like:this.state.like})
        console.log(this.props.lists)
      });
    }
}
const handleLoveList = () => { 
  if(this.state.love == true) {
    axios.get("/removeFromList/love/" + this.props.movie.id).then((res) => {
      this.setState({watch:this.state.watch,love:false,hate:this.state.hate,later:this.state.later,like:this.state.like})
      console.log(this.props.lists)
    });
  } else {
    axios.get("/addToList/love/" + this.props.movie.id).then((res) => {
      this.setState({watch:this.state.watch,love:true,hate:this.state.hate,later:this.state.later,like:this.state.like})
      console.log(this.props.lists)
    });
  }
}
const handleHateList = () => { 
  if(this.state.hate == true) {
    axios.get("/removeFromList/hate/" + this.props.movie.id).then((res) => {
      this.setState({watch:this.state.watch,love:this.state.love,hate:false,later:this.state.later,like:this.state.like})
      console.log(this.props.lists)
    });
  } else {
    axios.get("/addToList/hate/" + this.props.movie.id).then((res) => {
      this.setState({watch:this.state.watch,love:this.state.love,hate:true,later:this.state.later,like:this.state.like})
      console.log(this.props.lists)
    });
  }
}

  const rows = [
    createData("Tagline", this.props.movie.tagline),
    createData("Genres", getGenres(this.props.movie.genres)),
    createData("Budget", formatter.format(this.props.movie.budget)),
    createData("Popularity", this.props.movie.popularity),
    createData("Revenue", formatter.format(this.props.movie.revenue)),
    createData("Release Date", this.props.movie.release_date),
    createData("Language", getLanguages(this.props.movie.spoken_languages)),
    createData("Status", this.props.movie.status),
  ];
  return (
    <Box display="grid" gridTemplateColumns="repeat(12, 1fr)" gap={1}>
      <Box gridColumn="span 4">
        <Item>
          <ImageListItem key="favorite">
            <img
              src={this.props.movie.poster_path?`https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`:"https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"}
              srcSet={this.props.movie.poster_path?`https://image.tmdb.org/t/p/w500${this.props.movie.poster_path}`:"https://reactnativecode.com/wp-content/uploads/2018/02/Default_Image_Thumbnail.png"}
              alt={"Image"}
              loading="lazy"
            />
            <ImageListItemBar
              sx={{ background: "rgba(0,0,0,0)" }}
              actionIcon={
                (this.props.user !== "logged_out"?<div>
                  <IconButton
                    sx={this.state.watch?{ color: "rgba(255, 255, 255, 1)" }:{ color: "rgba(255, 255, 255, 0.54)" }}
                    onClick={handleWatchList}
                    aria-label={`Seen It`}
                  >
                    <VisibilityIcon />
                  </IconButton>
                  <IconButton
                    sx={this.state.later?{ color: "rgba(255, 255, 255, 1)" }:{ color: "rgba(255, 255, 255, 0.54)" }}
                    onClick={handleLaterList}
                    aria-label={`Watch Later`}
                  >
                    <WatchLaterIcon />
                  </IconButton>
                  <IconButton
                    sx={this.state.love?{ color: "rgba(255, 255, 255, 1)" }:{ color: "rgba(255, 255, 255, 0.54)" }}
                    onClick={handleLoveList}
                    aria-label={`Favorite`}
                  >
                    <FavoriteIcon />
                  </IconButton>
                  <IconButton
                    sx={this.state.like?{ color: "rgba(255, 255, 255, 1)" }:{ color: "rgba(255, 255, 255, 0.54)" }}
                    onClick={handleLikeList}
                    aria-label={`Like It`}
                  >
                    <ThumbUpIcon />
                  </IconButton>
                  <IconButton
                    sx={this.state.hate?{ color: "rgba(255, 255, 255, 1)" }:{ color: "rgba(255, 255, 255, 0.54)" }}
                    onClick={handleHateList}
                    aria-label={`Hate It`}
                  >
                    <ThumbDownAltIcon />
                  </IconButton>
                </div>:<></>)
              }
            />
          </ImageListItem>
          <Typography variant="h5" align="center" component="h4">
            {this.props.movie.title}
          </Typography>
          <List 
                  sx={{
                    maxHeight: 150,
                    overflow: "auto",
                    bgcolor: "background.paper",
                  }}>
          {this.props.streaming.results? this.props.streaming.results.map((item) => {
                    return (
                      <ListItem
                        key={item}
                      >
                          <ListItemAvatar>
                            <Avatar
                              alt={`https://image.tmdb.org/t/p/w500${item.logo_path}`}
                              src={`https://image.tmdb.org/t/p/w500${item.logo_path}`}
                            />
                          </ListItemAvatar>
                          <ListItemText
                            primary={`${item.provider_name}`}
                          />
                      </ListItem>
                    );
                  }):<></>}</List>
        </Item>
      </Box>
      <Box
        gridColumn="span 8"
        sx={{
          overflow: "auto",
        }}
      >
        <Item>
          <Grid container spacing={1}>
            <Grid item xs={12}>
              <Typography variant="body2" gutterBottom component="h5">
                {this.props.movie.overview}
              </Typography>

              <TableContainer component={Paper}>
                <Table aria-label="simple table">
                  <TableBody>
                    {rows.map((row) => (
                      <TableRow
                        key={row.name}
                        sx={{
                          "&:last-child td, &:last-child th": { border: 0 },
                        }}
                      >
                        <TableCell component="th" scope="row">
                          {row.name}
                        </TableCell>
                        <TableCell align="right">{row.value}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="h6" align="center" component="h4">
                Cast
              </Typography>
              {this.props.credit ? (
                <List
                  dense
                  sx={{
