//import logo from "./logo.svg";
import * as React from "react";
import Typography from "@mui/material/Typography";
import axios from "axios";

export default class MovieStats extends React.Component {
  state = {
    movies_seen: "loading",
    total_watch_time: "loading",
    average_movie_rating: "loading",
    average_popularity: "loading",
  };
  componentDidMount() {
    console.log(this.props);
    axios.get("/getMovieStats").then((res) => {
      console.log(res["data"]);
      const stats = res["data"];
      this.setState(stats);
      console.log(this.state);
    });
  }
  render() {
    return (
      <div>
        <Typography variant="h5" align="left" component="h5">
          {this.props.user}'s Movie Stats
        </Typography>
        <Typography variant="overline" display="block" gutterBottom>
          Movies Watch: {this.state.movies_seen}
        </Typography>
        <Typography variant="overline" display="block" gutterBottom>
            Total Watch Time: {this.state.total_watch_time}
        </Typography>
        <Typography variant="overline" display="block" gutterBottom>
          Average Movie Rating: {this.state.average_movie_rating}
        </Typography>
        <Typography variant="overline" display="block" gutterBottom>
          Average Movie Popularity: {this.state.average_popularity}
        </Typography>
      </div>
    );
  }
}
