import * as React from 'react';
import FormatBoldIcon from '@mui/icons-material/FormatBold';
import FormatItalicIcon from '@mui/icons-material/FormatItalic';
import FormatUnderlinedIcon from '@mui/icons-material/FormatUnderlined';
import FormatColorFillIcon from '@mui/icons-material/FormatColorFill';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';

export default function RatingSelect() {
  const [formats, setFormats] = React.useState(() => ['G', 'PG', 'PG-13', 'R', 'NC-17', 'NR']);

  const handleFormat = (event, newFormats) => {
    setFormats(newFormats);
  };

  return (
    <ToggleButtonGroup
        size="small"
      value={formats}
      onChange={handleFormat}
      aria-label="Rating"
    >
      <ToggleButton value="G" aria-label="G">
        G
      </ToggleButton>
      <ToggleButton value="PG" aria-label="PG">
        PG
      </ToggleButton>
      <ToggleButton value="PG-13" aria-label="PG-13">
        PG-13
      </ToggleButton>
      <ToggleButton value="R" aria-label="R">
        R
      </ToggleButton>
      <ToggleButton value="NC-17" aria-label="NC-17">
        NC-17
      </ToggleButton>
      <ToggleButton value="NR" aria-label="NR">
        NR
      </ToggleButton>
    </ToggleButtonGroup>
  );
}
